from pwn import *
import random
import subprocess

def fuzz():
	s=str(random.random())
	r=str(random.random())
	cmd="echo " + "" + " | " + "zzuf -s " + s + " -r " + r 
	return(subprocess.check_output(cmd, shell=True))

